import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from './../actions/index';
class TaskForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            id     : '',
            name   : '',
            status : ''
        }
    }
    onCloseForm = () => {
        this.props.onCloseForm();
    }

    onChange = (event) => {
         var target = event.target;
         var name   = target.name;
         var value  = target.value;
         if(name === 'status'){
            value = target.value === 'true' ? true :false;
         }
         this.setState({
              [name] : value
         });
    }

    onSave = (event) => {
        event.preventDefault();
        this.props.onSaveTask(this.state)
        this.onClose();
        this.onCloseForm();
    }

    onClose = () => {
        this.setState({
            name   : '',
            status : true
        });
    }

    componentWillMount(){
            if(this.props.editItem){
                this.setState({
                      id     : this.props.editItem.id,
                      name   : this.props.editItem.name,
                      status : this.props.editItem.status
                });
            } else{
                this.onClear();
            }
    }

    componentWillReceiveProps(nextProps){
        console.log("nextProps :");
            if(nextProps && nextProps.editItem){
                this.setState({
                    id     :  nextProps.editItem.id,
                    name   :  nextProps.editItem.name,
                    status :  nextProps.editItem.status
                });
            } else {
                 this.onClear();
            }
    }

    onClear = () => {
        console.log("data clear");
        this.setState({
               id     : '',
               name   : '',
               status : ''
        });
    }
    render() {
        var { id } = this.state
        if(!this.props.isDisplayFrom) return null;
        return (
            <div className="panel panel-warning">
                <div className="panel-heading">
                    <h3 className="panel-title">
                       { id !== '' ? 'Cập Nhập Công Việc' : 'Thêm Công Việc' }
                        <span
                            className="fa fa-times-circle text-right"
                            onClick={ this.onCloseForm }
                        ></span>
                    </h3>
                </div>
                <div className="panel-body">
                    <form onSubmit={this.onSave} >
                        <div className="form-group">
                            <label>Tên :</label>
                            <input
                                type="text"
                                className="form-control"
                                name="name"
                                value={this.state.name}
                                onChange = {this.onChange}
                            />
                        </div>
                        <label>Trạng Thái :</label>
                        <select
                            className = "form-control"
                            name      = "status"
                            value     = {this.state.status}
                            onChange  = {this.onChange}
                        >
                            <option value={true}>Kích Hoạt</option>
                            <option value={false}>Ẩn</option>
                        </select><br/>
                        <div className="text-center">
                            <button type="submit" className="btn btn-warning">
                                <span className="fa fa-plus mr-5"></span>Lưu Lại
                            </button>&nbsp;
                            <button type="button" className="btn btn-danger" onClick={this.onClose}>
                                <span className="fa fa-close mr-5"></span>Hủy Bỏ
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
      return {
        isDisplayFrom : state.isDisplayForm,
        editItem      : state.editItem
      }
};

const mapDispatchToProps = (dispatch, props) => {
       return {
            onSaveTask : (task) => {
                dispatch(actions.saveTask(task))
            },
            onCloseForm : () => {
              dispatch(actions.closeForm());
            }
       }
}

export default connect(mapStateToProps, mapDispatchToProps)(TaskForm);
