var initialState = {
        by    : 'name',
        value : 1

}


var myReducer = (state = initialState, action) => {
       if(action.type === 'SORT'){
          var { by, value } = action.sort; //by = action.sort.by
          // var { status }    = state;
          return {
              status : status,
              sort   : {
                by     :  by,
                value  :  value
              }
          }
       }

       return state;
}

export default myReducer
