import React, { Component } from 'react';
import './App.css';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import TaskControl from './components/TaskControl';

class App extends Component {
    constructor (props) {
        super(props);
        this.state = {
            tasks : [],
            isDisplayFrom : false
        }
    }

    componentWillMount(){
        if(localStorage && localStorage.getItem('tasks')){
               var tasks = JSON.parse(localStorage.getItem('tasks'));
               this.setState({
                    tasks: tasks
               });
        }
    }

    onGenerateData = () => {
        var tasks = [
            {
                id     : this.generateID(),
                name   : 'Hoc lap trinh',
                status : true
            },
            {
                id     : this.generateID(),
                name   : 'Di boi',
                status : false
            },
            {
                id     : this.generateID(),
                name   : 'Ngu',
                status : true
            }
        ];

        localStorage.setItem('tasks', JSON.stringify(tasks));

    }

    onToggleForm = () => {
          this.setState({
              isDisplayFrom : !this.state.isDisplayFrom
          })
    }

    s4(){
         return Math.floor((1* Math.random() ) * 0x10000).toString(16).substring(1);
    }

    generateID(){
           return this.s4() + this.s4() + '-' + this.s4();
    }

    onCloseForm = () => {
        this.setState({
            isDisplayFrom: false
        })
    }

    onSubmit = (data) => {
          data.id = this.generateID();
          var {tasks} = this.state;
          tasks.push(data);
          this.setState({
              tasks: tasks
          });
          localStorage.setItem('tasks', JSON.stringify(tasks));
    }

    onUpdateStatus = (id) => {
        var index = this.findIndex(id);
        var { tasks } = this.state;
        if(index !== -1){
              tasks[index].status = !tasks[index].status;
              this.setState({
                 tasks: tasks
              })
              localStorage.setItem('tasks', JSON.stringify(tasks));
        }
    }

    onDelete = (id) => {
        var index = this.findIndex(id);
        var { tasks } = this.state;
        if(index !== -1){
            tasks.splice(index,1);

        }
    }

    findIndex = (id) => {
        var {tasks} = this.state;
        var result = -1;
        tasks.forEach((task, index) => {
            if(task.id === id){
                result = index;
            }
        });
        return result;
    }

    render() {
        var { tasks, isDisplayFrom  } = this.state; //var tasks = this.state.tasks
        var elementTaskForm = isDisplayFrom ? <TaskForm onSubmit = { this.onSubmit }  onCloseForm = { this.onCloseForm } /> : '';
        return (
            <div className="container">
                <div className="text-center">
                    <h1>Quản Lý Công Việc</h1><hr/>
                </div>
                <div className="row">
                    <div className={ isDisplayFrom ? 'col-xs-4 col-sm-4 col-md-4 col-lg-4' : '' }>
                        {elementTaskForm}
                    </div>
                    <div className= {isDisplayFrom ? 'col-xs-8 col-sm-8 col-md-8 col-lg-8' : 'col-xs-12 col-sm-12 col-md-12 col-lg-12' }>
                        <button
                        type="button"
                        className="btn btn-primary"
                        onClick={this.onToggleForm} >
                            <span className="fa fa-plus mr-5"></span>
                            Thêm Công Việc
                        </button>
                        <button
                        type="button"
                        className="btn btn-danger ml-5"
                        onClick={this.onGenerateData}>
                            <span className="fa fa-plus mr-5"></span>
                            Generate Data
                        </button>

                        <TaskControl />
                        <TaskList onUpdateStatus = { this.onUpdateStatus } tasks = { tasks }  onDelete = { this.onDelete } />
                    </div>
                </div>
            </div>
        );
    }
}


export default App;
